    int packet_pending; //标记当前pkt未被成功消费，后续需要重新处理
    SDL_cond *empty_queue_cond; //绑定read_thread线程的continue_read_thread
    int64_t start_pts;
    AVRational start_pts_tb;
    int64_t next_pts;
